!(function () {

    $(document).ready(function () {
        new WOW().init();
    }),
        $(document).ready(function () {
            $(
                ".banner-content h1, .banner-content p, .banner-content a"
            ).addClass("animate");
        }),
        $(".partner-slider").owlCarousel({
            items: 7,
            loop: !0,
            autoplay: !0,
            dots: !1,
            margin: 24,
            responsive: {
                0: { items: 3, margin: 30 },
                768: { items: 5 },
                1200: { items: 7, margin: 24 },
            },
        });
    var e = $(".client-reviews").owlCarousel({
        loop: !0,
        autoplay: !0,
        items: 3.25,
        margin: 20,
        nav: !1,
        smartSpeed: 500,
        mouseDrag: !1,
        responsive: {
            0: { dotsEach: 3, items: 1.25 },
            575: { items: 2.25 },
            992: { items: 3 },
            1099: { dotsEach: 2, itesm: 3.25 },
        },
    });

    $(".owl-dots button").attr("aria-label", "owl-btn");
    var i = $(".client-reviews .owl-item.active");
    $(i[0]).addClass("middle"),
        e.on("changed.owl.carousel", function (e) {
            $(".client-reviews .owl-item").each(function (e, i) {
                $(i).hasClass("middle") && $(i).removeClass("middle");
            }),
                (i = $(".client-reviews .owl-item.active")),
                $(i[1]).addClass("middle");
        }),
        document.querySelector(".nav-toggle").addEventListener("click", () => {
            $(".ams-nav-menu").slideToggle("200");
        }),
        $("li.menu-item-has-children").on("click", function () {
            $(this).find(".sub-menu").slideToggle("200");
    });

    var videoLightbox = $('.vbox-item')

    if (videoLightbox.length) {
        videoLightbox.magnificPopup({
            type: 'iframe',
            mainClass: 'mfp-fade',
            removalDelay: 160,
            preloader: false,
            fixedContentPos: false,
            iframe: {
                patterns: {
                    youtube: {
                        index: 'youtube.com/',
                        id: 'v=',
                        src: '//www.youtube.com/embed/%id%?autoplay=1'
                    }
                },
                srcAction: 'iframe_src',
            }
        });
    }

    $(".view-ftur-btn").on("click", function(e){

        e.preventDefault();
        $(this).css("display", "none")
        $(".single-feature.hide").removeClass("hide")
    })

})(jQuery);
